%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 0 - Boas práticasclear all;clc;close all;%%% Pacotespkg load symbolic;                       % Somente para os que usam pkg load signal;pkg load control;                                   %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 1 - Mensagem%% Am  = 1;                  % amplitude da mensagemfm  = 1;                  % frequência da mensagem em Hzwm  = 2*pi*fm;            % frequência angular ad mensagemTm  = 1/fm;               % período de amostragem%%% Definir o vetor tempoNp    = 3;Npp   = 1000;tempo = linspace(0,Np*Tm,Npp*Np);DC    = 20;          %%% Mensagemmt = Am * square(wm*tempo,DC);%%% Visualizar a mensagemfigure(1)plot(tempo, mt, 'linewidth',3)%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 2 - Portadora%% Ac  = 1;                  % amplitude da mensagemfc  = 10;                 % frequência da mensagem em Hzwc  = 2*pi*fc;            % frequência angular ad mensagemTc  = 1/fc;               % período de amostragem%%% Portadoraec = Ac * cos(wc*tempo);%%% Visualizar a portadorafigure(2)plot(tempo, ec,'linewidth',3)%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 3 - Modulação AM%% 