%% Exercício 9.7%% Programa dividido em três partes%% %% Abordagem computacional %%%%%%%%%%%%%%%%%%%%%%%% %%%%%%% %%%%%%%%%%%%%%%%%%%%%%clear all;clc;close all;pkg load symbolic;                    % Usado somente para Octave%%%%%%%%%%%%%%%%%%%%%% Parte I %%%%%%%%%%%%%%%%%%%%%%%%%% Partimos da transformada de Fourier%%%%%%%%%%%%%%%%%%%%%%%% Parte I %%%%%%%%%%%%%%%%%%%%%%X = @(w) (sinc(2*w/pi)).^2%%% dividimos por pi pois este fator está imbutido%%% no cálculo pelo Octave %% -- sinc (X)%     Compute the sinc function.%%     Return sin (pi*x) / (pi*x).%%%%%%%%%%%%%%%%%%%%%% Parte II %%%%%%%%%%%%%%%%%%%%%%%%%% Vetor frequência%%%%%%%%%%%%%%%%%%%%%%%% Parte II %%%%%%%%%%%%%%%%%%%%%%N  =  10;n  =  [-10:1:10];To =  8;wo =  2*pi/To;w  =  n*wo;Dn = X(w)/To;%% Visualizando o espector de Amplitude figure(1) stem(w/(2*pi),abs(Dn),"linewidth", 3);                          % Na frequência o sinal é discretotitle('Serie de Fourier do sinal g(t)');xlabel('Frequencia em Hz');ylabel('Amplitude em  volts')grid;set(gca, "linewidth", 3, "fontsize", 23)%%%%%%%%%%%%%%%%%%%%%% Parte III %%%%%%%%%%%%%%%%%%%%%%%%%% Sintetizando o sinal%%%%%%%%%%%%%%%%%%%%%%%% Parte III %%%%%%%%%%%%%%%%%%%%%%%%%%%% Vetor tempo para visualização do sinal%%% diferente da variável simbólica t%%% para efeito de organização da solução%%% existem outros caminhos M = 1000; Ts = To/M; tempo = [-To:Ts:To];                        % Três períodos do sinal %% Sintetizando o sinalaux  = 0;             for k = 0 : 2*N          aux = aux + Dn(k+1)*exp(j*n(k+1)*wo*tempo);  endgr = aux;%% Visualizando o sinal reconstruidofigure(2)plot(tempo,gr,"linewidth", 3)title('Reconstrucao do sinal g(t)');xlabel('Tempo em segundos');ylabel('Amplitude em  volts')grid;set(gca, "linewidth", 3, "fontsize", 23)