%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 0 - Proposta para a segunda parte do trabalho.%% %%    a. Cada aluno gravar 3 vezes cada vogal e determinar: fo, f1 e f2 para cada gravação.%%      a.1 3 * 3 * 5 --> 45 valores --> a,e,i,o,u%%    b. Agrupar as informações da turma --> por exemplo: criar uma planilha comum (csv).%%      b.1 Número de alunos * 45 valores - Base de conhecimento.%%    c. Você gerar 5 arquivos, um para cada vogal, que não serõa usados no treinamento -%%    não está na base de conhecimento - usado como validação (teste).%%      c.1 1 --> acerto e 0 --> erro: 1 0 1 1 0 para cada aluno.%%    d. Análise: %%        d.1 histogramas de cada frequência para cada vogal: Média e a variância%%        d.2 a taxa de erro / acerto do sistema de reconhecimento.%%%%%%  Entrega 23/03 até 23:59 - individual%%%%  Reposição 07/04 e 14/04%%%%  %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 1 - Preparação do código %% %% Boas práticas: limpeza de variáveis; variáveis globais%% Constantes; carregar bibliotecas;...%%%%% Limpezaclc;          % limpa visual da tela de comandosclose all;    % limpa as figurasclear all;    % limpa as variáveis%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 2 - Sinal: calibração e sinal real%%%% trabalhar com um sinal amostrado%% %%% sinal artificial de referência - calibrar o Fourier nas mesmas condições%%%% criar um senoide artificial: amplitude - Ap, frequência - fo e fase - theta%% %%   sinal = Ap * cos (2 * pi * fo * t + theta)%%% recuperar o sinal a partir de um arquivo - real [gk,fs] = audioread ('a.wav');      % transformei um arquivo .wav em um vetor g(k)                                     % recuperei a taxa de amostragem - fs%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%  3 - Pré - processamento%%%%%%  Xn = 1/N somatoria_{k=0}^{N-1} gk(k) exp(-j*n*k*2*pi/N)N       = length(gk);               % número de pontos do sinal em análiseTs      = 1/fs;                     % tempo de amostragemws      = 2*pi*fs;                  % frequência anngularduracao = N*Ts;                     % Duração do sinaltempo   = linspace(0,N*Ts,N);       % vetor tempo computacionalfmax    = fs/2;                     % frequência máxima de amostragemfrequencia = linspace(-fs/2,fs/2,N);% vetor de frequências de Fourierresolucao  = fs/N;                  % resoluÇào em frequênciajanela = blackman(N);               % cria uma janela para melhorar o sinalgk = janela.*gk;                    % aplico a janela no sinal%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%  4 - Fourier%%%%%%  Xn = 1/N somatoria_{k=0}^{N-1} gk(k) exp(-j*n*k*2*pi/N) tic;                                % inicia um contador%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%            %%% estrutura matricialMatriz_J        = (exp(-j*2*pi/N))*ones(N,N);   % determino os coeficientesMatriz_nk       = [0:1:N-1]'*[0:1:N-1];         % determino os expoentesWN              = Matriz_J.^Matriz_nk;          % matriz de Fourier - constate para NXn              = WN*gk/N;                      % determina o Fourier - gk(coluna)Xn              = fftshift(Xn);                 % rotaciona o resultado de Fourier%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%toc;  %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%  5 -  Visualização%%%%figure(1)plot(tempo,gk,'k-','linewidth',3)     % configura plot(x,y, cor azul e linha cheia)xlabel('Tempo em segundos')           % tempo em segundosylabel('Amplitude')                   % amplitude em voltstitle('Sinal g(k) amostrado')         % títulogridfigure(2)plot(frequencia,log10(abs(Xn)),'k-','linewidth',3)                % configura plot(x,y, cor azul e linha cheia)xlabel('Frequência em Hz')                 % tempo em segundosylabel('Amplitude')                        % amplitude em voltstitle('Espectro de amplitude')             % títulogrid