%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 0 - Boas práticas%% %% Dicas para a prova de ECA414%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%clear all;      % limpar as variáveisclose all;      % fechga as figuras abertasclc;            % limpa a tela%%% carrega os pacotes pkg load control;pkg load signal;pkg load io;%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 1 - Leitura dos dados%% %% Leitura do arquivo csv%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% carrega as os valores do motormotor_DB   = csv2cell ("motor.csv");%%% separar as variáveis - fornecidovelocidade = {motor_DB{:,1}};posicao    = {motor_DB{:,2}};entrada    = {motor_DB{:,3}};velocidade = cell2mat(velocidade);posicao    = cell2mat(posicao);entrada    = cell2mat(entrada);%%% visualizar as variáveisTs = 1e-3;            % fornecidotempo = linspace(0,length(entrada)*Ts, length(entrada));figure(1)subplot(3,1,1); plot(tempo, entrada, 'linewidth',3);subplot(3,1,2); plot(tempo, velocidade, 'linewidth',3);subplot(3,1,3); plot(tempo, posicao, 'linewidth',3);%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 1 - Modelo dinâmico%% %% Função de transferência%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%tau_m = 0.01;        % constante de tempoKm    = 10;          % ganho do sistema%%% Função de transferênciaGpw    = tf(Km,[tau_m 1]);%%% simulando o modelo - velocidade / rotação - wvelocidade_modelo = lsim(Gpw,entrada,tempo);figure(2)subplot(3,1,1); plot(tempo, entrada, 'linewidth',3);subplot(3,1,2); plot(tempo, velocidade, 'linewidth',3);subplot(3,1,3); plot(tempo, velocidade_modelo, 'linewidth',3);%%% Função integralGi   = tf(1,[1 0]);  %%% simulando o modelo - posição posicao_modelo = lsim(Gpw*Gi,entrada,tempo);figure(3)subplot(3,1,1); plot(tempo, entrada, 'linewidth',3);subplot(3,1,2); plot(tempo, velocidade_modelo, 'linewidth',3);subplot(3,1,3); plot(tempo, mod(posicao_modelo,2*pi), 'linewidth',3);       