%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 0 - Boas práticas%% %% Dicas para a segunda parte do trabalho T2%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% prepara o ambiente de trabalhoclear all;clc;close all;%%% carrega pacotes específicospkg load io;pkg load signal;%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 1 - Trabalha com os dados - "banco de dados"%% %% Leitura de um arquivo csv%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% carrega as vogais em uma variável tipo cellvogais_DB = csv2cell ("Vogais.csv");%%% separa a coluna da classificaçãotipo_vogal = {vogais_DB{:,4}};%%% verifica as categorias presentescategorias = unique(tipo_vogal);%%% Localiza um tipo específico de vogalaux_tipo = strcmp(categorias(1),tipo_vogal)%%% separa a frequência fundamental - fofreq_fo = cell2mat({vogais_DB{aux_tipo,5}});%%% separa a formante f1freq_f1 = cell2mat({vogais_DB{aux_tipo,6}});%%% separa a formante f2freq_f2 = cell2mat({vogais_DB{aux_tipo,7}});