%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%  0 -  Laplace%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 1 - Preparação do código %% %% Boas práticas: limpeza de variáveis; variáveis globais%% Constantes; carregar bibliotecas;...%%%%% Limpezaclc;          % limpa visual da tela de comandosclose all;    % limpa as figurasclear all;    % limpa as variáveis%%% Laplace_Exemplopkg load control    % interprete a variável s = sigma + j w%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 2 - criando uma função de transferência%% %%% G(s) = 1/(1.s + 1) => G(s) = 1/(RC.s + 1) = (1/RC)/(s + (1/RC)) = a /(s + a).%%%% R ==> constante no tempo e C ==> constante no tempoNumerador    = [1];        % coeficientes do numeradorDenominador  = [1 1];      % coeficientes do denominador%%% Criação da função de transferência - tfGs           = tf(Numerador,Denominador);% Transfer function 'Gs' from input 'u1' to output ...%        1% y1:  -----%      s + 1% Continuous-time model.%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 3 - aplicando uma entrada X(s)%% %%% Impulso unitáriofigure(1)impulse(Gs)%%% Degrau unitáriofigure(2)step(Gs)%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 4 - Misturando com a programação%% parametro  = [0 1 2 3 4 5];               % Modificando o produto RC%%% G(s) = parametro /(1.s + parametro)for k = 1:length(parametro)    Numerador    = [parametro(k)];        % coeficientes do numerador    Denominador  = [1 parametro(k)];      % coeficientes do denominador%%% Criação da função de transferência - tf        clc;    Gs           = tf(Numerador,Denominador)     pause   end