%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 0 - Boas práticasclear all;                        % apaga todas as variáveisclose all;                        % fechar as figurasclc;                              % limpa o ambiente de simulaçãopkg load control;                % somente para o Octave%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 1 - Definir o sinal g(t)T0 = 1;                         % duração de 1 períodoN  = 1000;                      % número de pontos em um períodoM  = 10;                        % Número de períodosgt    = @(t)  exp(-t);          % sinal x(t)tempo = linspace(0,T0,N)        % vetor tempoxt    = gt(tempo)               % sinal basextT0  = [xt xt xt xt xt xt xt xt xt xt];  % sinal compostot     = linspace(0,M*T0,M*N);u = xtT0;                       % entrada do sistemaNum = 1;                        % NumeradorDen = [1 1];                    % DenominadorHs  = tf(Num, Den);             % função de transferência[Y, T, X] = lsim (Hs,u,t);      % saída%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 2 - Visualizando y(t)figure(1)plot(tempo,xt)figure(2)plot(t,xtT0)figure(3)plot(t,Y)