%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 0 - Boas práticasclear all;clc;close all;pkg load control ;                                    % Somente para os que usam                                                       % Octave%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 1 - sistema a ser estudado%%%% i = y/R + C dy/dt + 1/L int(y dt)%%% Primeiro caso C=1 R=1 e L=1N = [1 0];            % numerador de Laplace para o caso N(s) = sD = [1 1 1];          % denominador de Laplace D(s) = s^2 + s + 1                      % G(s) = s / s^2 + s + 1 = Y(s)/I(s)                      Gs = tf(N,D);         % Função de transferência%%% Aplicar um impulso i(t) = delta(t) --> I(s) = 1% G(s) = s / s^2 + s + 1 = Y(s) - resposta impulsiva%%% Y(s) quando a entrada é um impulsoimpulso = 1;Ysi     = Gs*impulso%%% Y(s) quando a entrada é um degrau - I(S) = 1/s% Ysd = (s / s^2 + s + 1)*(1/s) = (1 / s^2 + s + 1)Degrau = tf([1],[1 0]);    % criei o degrau Ysd     = Gs*Degrau%%% Análise das raizes ou pólospolos = roots(D);       % determina as raizes do denominador %%% Primeiro caso T = 1 segundo quais os valores de R,L e C?%%% Quero reduzir a estabilização de 10 segundos para 4 segundos?%%% Quero o sistema sem oscilação, quais os valores de R,L e C?%%% R,L,C = 1 ==> T = 7.3 segundos aproximadamente