%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 0 - Boas práticasclear all;clc;close all;pkg load control ;                                    % Somente para os que usam                                                       % Octave%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 1 - sistema a ser estudado%%%% vg(t) = R i(t) + 1/C int[i(t) dt] + L di(t)/dt%%%%%%% Influência do resitor - RR     = [0:0.1:10];L     = 1;C     = 1;%%% padronizar o tempotempo = linspace(0,10,1000);for k = 1:100%%% Função de transferência - G(s)  N    = [1/L 0];  D    = [1 R(k)/L 1/(L*C)];  Gs = tf(N,D);  %%% Análise do polo do sistema - n = 2  - ordem do sistema  polo    = roots(D);  pr(k,1) = polo(1);  pr(k,2) = polo(2);      Real_polo1 = real(pr(:,1));  Real_polo2 = real(pr(:,2));    Imag_polo1 = imag(pr(:,1));  Imag_polo2 = imag(pr(:,2));  %%% estimulando a função com entrada impulso  yr(:,k)   = impulse(Gs,tempo);  end%%% Visualizaçãofigure(1)subplot(1,2,1)surf(yr);xlabel('Valor da resitencia em ohms')ylabel('Valor do tempo em segundos')zlabel('Amplitude')subplot(1,2,2)plot(Real_polo1,Imag_polo1, 'linewidth',3, 'bx', Real_polo2,Imag_polo2, 'linewidth',3, 'rx')xlabel('Real')ylabel('Imaginario')%%% Influência do resitor - CR     = 0;L     = 1;C     = [0.01:0.01:0.1];%%% padronizar o tempotempo = linspace(0,10,1000);for k = 1:10%%% Função de transferência - G(s)  N    = [1/L 0];  D    = [1 R/L 1/(L*C(k))];  Gs = tf(N,D);  %%% Análise do polo do sistema - n = 2  - ordem do sistema  polo    = roots(D);  pc(k,1) = polo(1);  pc(k,2) = polo(2);    %%% estimulando a função com entrada impulso  yc(:,k)   = impulse(Gs,tempo);  end%%% Visualizaçãofigure(2)surf(yc);xlabel('Valor da capacitor em faraday')ylabel('Valor do tempo em segundos')zlabel('Amplitude')%%% Conclusões: a resitência controla a atenuação e a capacitância controla o socilação