%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 0 - Boas práticasclear all;clc;close all;%%% Pacotes  necessáriospkg load signal;                         % laplacepkg load control ;                       % Somente para os que usam                                          % Octave%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 1 - sistema a ser estudado - no tempo%%%% vg(t) = L di(t)/dt + 1/C int[i(t)dt] + R i(t)%%% Caso 1: R = 1, L = 1 e C = 1 --> vg(t) = delta(t)N   = [1 0];        % numeradorD   = [1 1 1];      % denominadorGs  = tf(N,D)       % ganho do sistema% Transfer function 'Gs' from input 'u1' to output ...%           s% y1:  -----------%      s^2 + s + 1% Continuous-time model.%%% Determinar a saidaIsi   = Gs*1;        % determino a saídaspect%%% Visualizando o i(t)figure(1)impulse(Gs);        % é anti transformada de Lapace %%% Conclusões% sistema oscilatório amortecido com convergência em torno de 10 segundos%%% Caso 2 - igual ao caso 1 com entrada vg(t) = u(t) ... degrau unitário%%% Determinar a saidaVG   = tf(1,[1 0]); % criar o degrau unitárioIsd   = Gs*VG;        % determino a saídaspect%%% Visualizando o i(t)figure(2)step(Gs);        % é anti transformada de Lapace %%% relação entre as saídas%%% Isi = s * Isd - a resposta impulsiva é a derivada da resposta ao degrau  